CREATE TABLE Account (
    username NVARCHAR(200) PRIMARY KEY,
    password NVARCHAR(200),
    hoatDong NVARCHAR(200),
    chucvu NVARCHAR(200)
);


CREATE TABLE KhachHang (
    sdt NVARCHAR(200) PRIMARY KEY REFERENCES Account(username),
    tenKhachHang NVARCHAR(200),
    diaChi NVARCHAR(200),
    email NVARCHAR(200),
    gioiTinh NVARCHAR(200),
	CONSTRAINT FK_KhachHang_Account FOREIGN KEY (sdt) REFERENCES Account(username)
);

CREATE TABLE NhanVien (
    idNhanVien INT PRIMARY KEY IDENTITY(1,1),
    username NVARCHAR(200) FOREIGN KEY REFERENCES Account(username),
    tenNhanVien NVARCHAR(200),
    soDienThoai NVARCHAR(200),
    diaChi NVARCHAR(200),
    email NVARCHAR(200),
    CONSTRAINT FK_NhanVien_Account FOREIGN KEY (username) REFERENCES Account(username)
);


CREATE TABLE ChucVu (
    idNhanVien INT FOREIGN KEY REFERENCES NhanVien(idNhanVien),
    tenChucVu NVARCHAR(200),
    moTa NVARCHAR(MAX),
    PRIMARY KEY (idNhanVien, tenChucVu),
	CONSTRAINT FK_ChucVu_NhanVien FOREIGN KEY (idNhanVien) REFERENCES NhanVien(idNhanVien)
);

CREATE TABLE DanhMucSanPham (
    idDanhMucSanPham INT PRIMARY KEY,
    theloai Nvarchar(200),
    tenLoaiSanPham NVARCHAR(200)
);


CREATE TABLE Mau (
    idMau INT PRIMARY KEY,
    mau NVARCHAR(200)
);

CREATE TABLE SanPham (
    idSanPham INT PRIMARY KEY,
    idDanhMucSanPham INT FOREIGN KEY REFERENCES DanhMucSanPham(idDanhMucSanPham),
    idMau INT FOREIGN KEY REFERENCES Mau(idMau),
    img NVARCHAR(MAX),
    title NVARCHAR(200),
    giaBan FLOAT,
    giamGia FLOAT,
    moTa NVARCHAR(MAX),
    ngayNhap NVARCHAR(200),
	CONSTRAINT FK_SanPham_DanhMuc FOREIGN KEY (idDanhMucSanPham) REFERENCES DanhMucSanPham(idDanhMucSanPham),
	CONSTRAINT FK_SanPham_Mau FOREIGN KEY (idMau) REFERENCES Mau(idMau)
);

CREATE TABLE ProductImage (
  idSanPham INT NOT NULL ,
  img NVARCHAR(MAX) NOT NULL,
  CONSTRAINT FK_ProductImage_Product FOREIGN KEY (idSanPham) REFERENCES SanPham(idSanPham)
);




CREATE TABLE KichCo (
    idKC INT PRIMARY KEY,
    idSanPham INT FOREIGN KEY REFERENCES SanPham(idSanPham),
    size NVARCHAR(200),
    soLuong INT,
	CONSTRAINT FK_KichCo_SanPham FOREIGN KEY (idSanPham) REFERENCES SanPham(idSanPham)
);


CREATE TABLE DanhSachHoaDon (
    idDSHD INT PRIMARY KEY IDENTITY(1,1),
    idSanPham INT FOREIGN KEY REFERENCES SanPham(idSanPham),
    soDienThoaiKH NVARCHAR(200) FOREIGN KEY REFERENCES KhachHang(sdt),
    idNhanVien INT FOREIGN KEY REFERENCES NhanVien(idNhanVien),
    ngayBan NVARCHAR(200)
    
);


CREATE TABLE ChiTietHoaDon (
    idHoaDon INT PRIMARY KEY REFERENCES DanhSachHoaDon(idDSHD),
    idSanPham INT FOREIGN KEY REFERENCES SanPham(idSanPham),
    giaBan FLOAT,
    soLuong INT,
    
);


CREATE TABLE DatHang (
    idHoaDon INT PRIMARY KEY REFERENCES DanhSachHoaDon(idDSHD),
    diaChiNhanHang NVARCHAR(1000),
    soDienThoaiNhanHang NVARCHAR(200),
    hinhThucThanhToan NVARCHAR(200),
);


CREATE TABLE GioHang (
    idGH INT PRIMARY KEY IDENTITY(1,1),
    soDienThoaiKH NVARCHAR(200) FOREIGN KEY REFERENCES KhachHang(sdt),
    idSanPham INT FOREIGN KEY REFERENCES SanPham(idSanPham),
    soLuong int
);